<script>

    const tableContainer = document.getElementById('table-container');
    const tableBody = document.getElementById('table-body');
    const paginationContainer = document.getElementById('pagination-container');
    const itemsPerPage = 20; // Número de registros por página
    const tabla = <?= json_encode($periodos); ?>; // Los datos de PHP


    function showPage(page) {
        tableBody.innerHTML = '';
        const startIndex = (page - 1) * itemsPerPage;
        const endIndex = startIndex + itemsPerPage;
        const baseUrl = "<?php echo base_url(); ?>";

        for (let i = startIndex; i < endIndex && i < tabla.length; i++) {
            const lista = tabla[i];
            const row = document.createElement('tr');
            const options = { day: '2-digit', month: 'long', year: 'numeric' };
    const fechaInicio = new Date(lista.fecha_inicio_quincena.replace(/-/g, '/')).toLocaleDateString('es-ES', options);
    const fechaFin = new Date(lista.fecha_fin_quincena.replace(/-/g, '/')).toLocaleDateString('es-ES', options);
            row.innerHTML = `
      <td>${i + 1}</td>
      <td>${lista.periodo}</td>
      <td>${lista.mes}</td>
      <td>${fechaInicio}</td>
      <td>${fechaFin}</td>
      <td>${lista.colaboradores} colaboradores</td>
      <td>
  ${ (lista.nombre_f1 && lista.nombre_f2 && lista.nombre_f3 && lista.nombre_f4) ? '<span class="status-circle"></span> SI' : '<span class="status-circle1"></span> NO' }
    </td>
    <td>
  ${(lista.pagada === 'SI') ? '<span class="status-circle"></span> SI' : '<span class="status-circle1"></span> NO'}  
</td>


      <td>
  ${lista.nombre_f1 ? '<img src="data:image/png;base64," alt="MDN"  width="14" height="14"/> <strong style="color:#3498db;">Jessica</strong>' : '<img src="https://w7.pngwing.com/pngs/380/139/png-transparent-x-red-mark-incorrect-thumbnail.png" alt="MDN"  width="14" height="14"/> <strong style="color:red;">Jessica</strong>'}<br>
  ${lista.nombre_f2 ? '<img src="data:image/png;base64," alt="MDN"  width="14" height="14"/> <strong style="color:#3498db;">Admin</strong>' : '<img src="https://w7.pngwing.com/pngs/380/139/png-transparent-x-red-mark-incorrect-thumbnail.png" alt="MDN"  width="14" height="14"/> <strong style="color:red;">Admin</strong>'}<br>
  ${lista.nombre_f3 ? '<img src="data:image/png;base64," alt="MDN"  width="14" height="14"/> <strong style="color:#3498db;">RH</strong>' : '<img src="https://w7.pngwing.com/pngs/380/139/png-transparent-x-red-mark-incorrect-thumbnail.png" alt="MDN"  width="14" height="14"/> <strong style="color:red;">RH</strong>'}<br>
  ${lista.nombre_f4 ? '<img src="data:image/png;base64," alt="MDN"  width="14" height="14"/> <strong style="color:#3498db;">Alejandro</strong>' : '<img src="https://w7.pngwing.com/pngs/380/139/png-transparent-x-red-mark-incorrect-thumbnail.png" alt="MDN"  width="14" height="14"/> <strong style="color:red;">Alejandro</strong>'}
</td>

      <td>
        <button type='button' class="btn ver-periodo-btn btn-xs btn-radius" 
            title="Crear periodos" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">Acciones 
            <i class='fas fa-caret-down'></i>
        </button>
        <div class="dropdown-menu acciones">
        <a href="${baseUrl}home/board/${lista.periodo}/${lista.mes}/${lista.pagada}/${lista.fecha_inicio_quincena}/${lista.fecha_fin_quincena}"  >&nbsp; <i class="fa fa-arrow-circle-right"></i> Ver periodo</a>
        <br>
        <a href="${baseUrl}home/deletePeriodo/${lista.periodo}/${lista.mes}/${lista.fecha_inicio_quincena}/${lista.fecha_fin_quincena}">&nbsp; <i class='fas fa-trash'></i> Eliminar periodo </a>
        </td>
    `;
            tableBody.appendChild(row);
        }
    }

    function generatePagination() {
        paginationContainer.innerHTML = '';
        const totalPages = Math.ceil(tabla.length / itemsPerPage);

        for (let i = 1; i <= totalPages; i++) {
            const pageLink = document.createElement('a');
            pageLink.href = '#';
            pageLink.textContent = i;

            pageLink.addEventListener('click', (event) => {
                event.preventDefault();
                showPage(i);
            });
            paginationContainer.appendChild(pageLink);
        }
    }

    showPage(1);
    generatePagination();

</script>